svg.script:


		//funciones cross browser
function removeEvent(elemento,evento,funcion) {
 if (elemento.removeEventListener) 
	elemento.removeEventListener (evento,funcion,false);
 if (elemento.detachEvent)
	elemento.detachEvent ('on'+evento,funcion); 
}
function addEvent(elemento, evento, funcion){
	if(elemento.addEventListener){
		elemento.addEventListener(evento, funcion);
	} else if(elemento.attachEvent){
		elemento.attachEvent('on'+evento, funcion);
	}
}
function insertAfter(newNode, referenceNode) {
	referenceNode.parentNode.insertBefore(newNode, referenceNode.nextSibling);
}
//otras funciones
function c(str){return console.log(str);}
function  obtID(str){ return document.getElementById(str); }
function crear(str){ return document.createElement(str);} 
//_______________________________________

var c = obtID('Capa_1');
addEvent(c,'click',enfocar);
console.log(document.getElementsByTagName('path')[0])
function enfocar(){ 

	c.getElementsByTagName('path')[0].setAttribute('transform','translate(0) rotate(45 50 50)');
	c.getElementsByTagName('path')[2].setAttribute('transform','translate(-111) rotate(-45 62 276)');
	c.getElementsByTagName('path')[1].setAttribute('stroke','rgba(128, 60, 60, 0)');


	removeEvent(this,'click',enfocar);
	addEvent(this,'click',desenfocar);
}
function desenfocar(){

	c.getElementsByTagName('path')[0].setAttribute('transform','translate(0) rotate(45 50 50)');
	c.getElementsByTagName('path')[2].setAttribute('transform','translate(-111) rotate(-45 62 276)');

	removeEvent(this,'click',desenfocar);
	addEvent(this,'click',enfocar);
}
